import styled from 'styled-components';

export const ButtonWrapper = styled.button`
    font-family: 'Press Start 2P', cursive;
    background-color: #03318c;
    color: white;
    border-radius: 5px;
    border: 3px solid #03318c;
    width: 200px;
    height: 30px;
    margin: 10px;
    cursor: pointer;
`;
